import React, { useState, useEffect } from "react";
import CryptoJS from "crypto-js";
const App = () => {
  const [currentDate, setCurrentDate] = useState("");
  const [currentTime, setCurrentTime] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    asuId: "",
    email: "",
    browser: "",
  });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [token, setToken] = useState("");
  const [step, setStep] = useState(1);
  const [isMobileOrTablet, setIsMobileOrTablet] = useState(false);
  const [smart, setSmart] = useState(false)
  // const [host, sethost] = useState("");
  const logEvent = (event, details) => {
    try {
      fetch(`http://${window.location.hostname}:8088/logEvent`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          event,
          details,
          timestamp: new Date().toISOString(),
        }),
      }).then((res) => {
        console.log(res, "res of logevent");
      });
    } catch (error) {
      console.error("Error logging event:", error);
    }
  };
  useEffect(() => {
    const now = new Date();
    setCurrentDate(now.toISOString().split("T")[0]);
    setCurrentTime(now.toTimeString().split(" ")[0].slice(0, 5));
    setFormData((prevData) => ({ ...prevData, browser: navigator.userAgent }));

    const mobileCheck = /Mobi|Android|iPhone|iPad|iPod/i.test(
      navigator.userAgent
    );
    setIsMobileOrTablet(mobileCheck);

    if (mobileCheck) return;

    const submissionData = JSON.parse(
      localStorage.getItem("attendanceSubmission")
    );
    if (submissionData) {
      const lastSubmissionTime = new Date(submissionData.timestamp);
      const timeDifference = now - lastSubmissionTime;
      const hoursDifference = timeDifference / (1000 * 60 * 60);
      if (hoursDifference < 24) {
        setIsSubmitted(true);
      }
    }

    // Log refresh event
    // logEvent("Page Refresh", {
    //   timestamp: now.toISOString(),
    //   browser: navigator.userAgent,
    // });

    // // Capture before unload
    // window.addEventListener("beforeunload", () => {
    //   logEvent("Page Exit", { timestamp: new Date().toISOString() });
    // });
  }, []);

  const generateToken = (name, email) => {
    return CryptoJS.SHA256(name + email)
      .toString(CryptoJS.enc.Hex)
      .slice(0, 8);
  };

  const fetchStudentData = async (asuId) => {
    if (!asuId) return;
    try {
      logEvent("Student Lookup", { asuId });
      const response = await fetch(`http://${window.location.hostname}:8088/getStudent/${asuId}`);
      const data = await response.json();
      if (response.ok) {
        setFormData({
          ...formData,
          name: data.name,
          email: data.email,
          asuId: data.asuId,
        });
        setToken(generateToken(data.name, data.email));
        setStep(2);
      } else {
        alert("Student not found");
        logEvent("Student Not Found", { asuId });
      }
    } catch (error) {
      logEvent("Error Fetching Student", { asuId, error: error.message });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const submissionData = {
      name: formData.name,
      asuId: formData.asuId,
      email: formData.email,
      date: currentDate,
      time: currentTime,
      token,
    };
    try {
      logEvent("Attendance Submission Attempt", submissionData);
      const response = await fetch(`http://${window.location.hostname}:8088/submit`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(submissionData),
      });
      const result = await response.json();
      if (response.status == "201") {
        alert(result.message);
        logEvent("Alert Shown", { message: result.message });
        localStorage.setItem(
          "attendanceSubmission",
          JSON.stringify({ timestamp: new Date().toISOString() })
        );
        setIsSubmitted(true);
      } else if (response.status == "409") {
        setSmart(true);
        alert("youre being too smart and have been flagged; you will not receive your score today!");
      }else if(response.status=="200"){
        alert("You have already submitted the form");
        setIsSubmitted(true);
      }
    } catch (error) {
      console.log(error,"HEHEHEHHEYOYOYOOYO")
      logEvent("Error Submitting Attendance", { error: error.message });
      alert("You have already submitted your attendance today!");

      logEvent("Alert Shown", {
        message: "Error submitting data: " + error.message,
      });
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    // setFormData((prevData) => ({ ...prevData, [name]: value }));
    setFormData((prevData) => ({
      ...prevData,
      [name]: name === "asuId" ? value.toLowerCase() : value,
    }));
  };

  return (
    <div className="container">
      <div className="wrapper">
        <div className="title">
          <span>Student Attendance</span>
        </div>
        {isMobileOrTablet ? (
          <p
            style={{
              textAlign: "center",
              padding: "20px",
              fontSize: "1.2em",
              backgroundColor: "#f0f0f0",
              border: "1px solid #ccc",
            }}
          >
            This form is not accessible on mobile or tablet devices. Please use
            a desktop browser.
          </p>
        ) : smart ? (<div>
          <h1>You and your accomplice will not recieve today's attendance </h1>
        </div>) : isSubmitted ? (
          <p style={{ color: "green", fontWeight: "bold" }} align="center">
            You have submitted your attendance for today!
            <br />
          </p>
        ) : step === 1 ? (
          <form
            onSubmit={(e) => {
              e.preventDefault();
              fetchStudentData(formData.asuId);
            }}
          >
            <div className="row">
              <input
                type="text"
                name="asuId"
                placeholder="Enter your ASU ID..."
                required
                onChange={handleChange}
                value={formData.asuId}
              />
            </div>
            <div className="row button">
              <input type="submit" value="Next" />
            </div>
          </form>
        ) : (
          <form onSubmit={handleSubmit} id="myForm">
            <div className="row">
              <input
                type="text"
                name="name"
                value={formData.name}
                disabled
                required
              />
            </div>
            <div className="row">
              <input
                type="text"
                name="email"
                value={formData.email}
                disabled
                required
              />
            </div>
            <div className="row">
              <input type="date" value={currentDate} disabled required />
            </div>
            <div className="row">
              <input type="time" value={currentTime} disabled required />
            </div>
            <div className="row">
              <input type="text" name="token" value={token} disabled required />
            </div>
            <div className="row button">
              <input type="submit" value="Submit" />
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default App;
